-- ----------------------------
-- Table structure for products
-- ----------------------------
DROP TABLE IF EXISTS `products`;
CREATE TABLE `products`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `slug` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `stock` int(11) NOT NULL DEFAULT 0,
  `price` int(11) NOT NULL DEFAULT 0,
  `sale` int(11) NOT NULL DEFAULT 0,
  `position` int(11) NOT NULL DEFAULT 0,
  `is_active` int(11) NOT NULL DEFAULT 1,
  `is_hot` int(11) NOT NULL DEFAULT 0,
  `category_id` int(11) NOT NULL DEFAULT 0,
  `parent_id` int(10) NULL DEFAULT NULL,
  `url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `brand_id` int(11) NOT NULL DEFAULT 0,
  `vendor_id` int(11) NOT NULL DEFAULT 0,
  `summary` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL,
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL,
  `user_id` int(11) NOT NULL DEFAULT 0,
  `unit` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `products_slug_index`(`slug`) USING BTREE,
  INDEX `name`(`name`) USING BTREE,
  INDEX `slug`(`slug`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 92 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of products
-- ----------------------------
INSERT INTO `products` VALUES (63, 'Bắp Cải', 'bap-cai', 'upload/product/1607532658_bap-cai.jpg', 700, 27000, 25000, 2, 1, 1, 64, 59, 'https://www.google.com.vn/?hl=vi', 26, 21, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Bắp cải, Cải bắp</strong> l&agrave; một loại rau chủ lực trong họ Cải - Người Ph&aacute;p gọi n&oacute; l&agrave; Su (Chon) n&ecirc;n từ đ&oacute; c&ograve;n c&oacute; t&ecirc;n l&agrave; S&uacute;. Bắp cải tim c&oacute; h&igrave;nh dạng thu&ocirc;n nhỏ, nhọn ở đầu, h&igrave;nh tr&aacute;i tim khi bổ đ&ocirc;i.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, mềm</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Bắp cải tim</strong> c&oacute; vị ngọt, t&iacute;nh m&aacute;t, ngo&agrave;i l&agrave; m&oacute;n ăn ngon ra c&ograve;n c&oacute; t&aacute;c dụng chữa được nhiều bệnh. Trong bắp cải c&oacute; chứa Vitamin A, C, U, canxi, kali, phốt pho, protein, calo v&agrave; một số kho&aacute;ng chất cần thiết cho sức khỏe.</li>\r\n	<li><strong>Bắp cải tim</strong> c&oacute; t&aacute;c dụng giảm cholestorol trong m&aacute;u, giảm nguy xơ vữa động mạch m&aacute;u, chống b&eacute;o ph&igrave;, chữa tiểu đường, giảm đau nhức, giải độc cơ thể, lọc m&aacute;u.</li>\r\n	<li><strong>Bắp cải tim</strong> cũng l&agrave;m giảm nguy cơ bị ung thư đặc biệt l&agrave; ung thư phổi, ung thư dạ d&agrave;y, ung thư v&uacute; v&agrave; tuyến tiền liệt ruột kết.</li>\r\n</ul>', 0, '800g', '2020-12-09 16:50:58', '2020-12-30 07:14:45');
INSERT INTO `products` VALUES (64, 'Bắp Cải Tím', 'bap-cai-tim', 'upload/product/1607541074_product-4.jpg', 700, 39000, 35000, 2, 1, 0, 64, 59, NULL, 26, 22, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Bắp cải t&iacute;m</strong> xuất xứ từ Địa Trung Hải, hiện nay được trồng rộng r&atilde;i khắp thế giới, th&iacute;ch hợp với kh&iacute; hậu &ocirc;n đới. Tại Việt Nam bắp cải t&iacute;m được trồng nhiều ở Đ&agrave; Lạt. Bắp cải t&iacute;m c&oacute; h&igrave;nh dạng tr&ograve;n, m&agrave;u t&iacute;m đậm.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Gi&ograve;n, ngọt</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Bắp cải t&iacute;m</strong> chứa lượng vitamin A v&agrave; C dồi d&agrave;o, rất tốt cho mắt v&agrave; da, nhiều antoxianin (chất tạo ra m&agrave;u t&iacute;m) chất n&agrave;y c&oacute; t&iacute;nh kh&aacute;ng vi&ecirc;m, bảo vệ tế b&agrave;o khỏi những tổn hại của tia cực t&iacute;m v&agrave; c&oacute; thể gi&uacute;p giảm nguy cơ mắc một số bệnh ung thư, chống oxy h&oacute;a gi&uacute;p bảo vệ c&aacute;c tế b&agrave;o n&atilde;o, ngăn ngừa bệnh mất tr&iacute; nhớ.</li>\r\n	<li><strong>Bắp cải t&iacute;m</strong> &iacute;t calories, th&iacute;ch hợp cho người ăn ki&ecirc;ng. Bắp cải t&iacute;m c&oacute; chứa một số chất c&oacute; thể l&agrave;m giảm nguy cơ ung thư v&uacute;, gi&uacute;p giảm nguy cơ bệnh tim, bệnh thấp khớp, tiểu đường, đồng thời chứa chất latic v&agrave; axit gi&uacute;p tẩy ruột.</li>\r\n	<li><strong>Bắp cải t&iacute;m</strong> gi&uacute;p l&agrave;n da th&ecirc;m mềm mịn, trắng s&aacute;ng v&agrave; giảm nguy cơ h&igrave;nh th&agrave;nh vết nhăn. Bổ sung loại rau n&agrave;y cho bữa ăn h&agrave;ng ng&agrave;y, bạn sẽ thấy hiệu quả l&agrave;m đẹp da v&ocirc; c&ugrave;ng kinh ngạc.</li>\r\n</ul>', 0, '800g', '2020-12-09 19:11:14', '2020-12-30 07:14:56');
INSERT INTO `products` VALUES (67, 'Cải thảo', 'cai-thao', 'uploads/product/1607542923_caithao_master.webp', 700, 26400, 25000, 3, 1, 1, 64, 59, NULL, 28, 23, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Cải thảo</strong> c&oacute; t&ecirc;n tiếng Anh l&agrave; Chinese Cabbage, được trồng nhiều tại Đ&agrave; Lạt. Bắp cải thảo h&igrave;nh trụ, tr&ograve;n,&nbsp;c&oacute; m&agrave;u sắc kh&aacute; giống với cải bắp, phần l&aacute; bao ngo&agrave;i của bắp cải thảo m&agrave;u xanh đậm, c&ograve;n l&aacute; cuộn ở b&ecirc;n trong (gọi l&agrave; l&aacute; non) c&oacute; m&agrave;u xanh nhạt, phần cuống l&aacute; c&oacute; m&agrave;u trắng. Cải thảo nặng trung b&igrave;nh 800gr, d&agrave;i 15 -25 cm.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, t&iacute;nh m&aacute;t</li>\r\n</ul>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-28px; position:absolute; top:-4.8px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Cải thảo</strong>, c&oacute; t&aacute;c dụng hạ kh&iacute;, thanh nhiệt nhuận thấp, c&oacute; c&ocirc;ng dụng l&agrave;m mềm cổ họng, bớt r&aacute;t, đỡ ho; lại bổ &iacute;ch trường vị, l&agrave; loại rau ngon chứa nhiều vitamin A, B, C, E.</li>\r\n	<li><strong>D&ugrave;ng cải thảo nấu canh</strong> cho người bị bệnh trường nhiệt, bệnh sốt r&eacute;t v&agrave; c&aacute;c bệnh c&oacute; sốt l&acirc;u, tuỳ &yacute; th&ecirc;m gi&aacute; đậu xanh hoặc gi&aacute; đậu n&agrave;nh, c&agrave;, rau cần, nấu chung, canh ăn bổ lại hạ sốt.</li>\r\n	<li><strong>Người bị bệnh vi&ecirc;m b&agrave;ng quang</strong>, vi&ecirc;m đường tiết niệu, tiểu tiện kh&ocirc;ng b&igrave;nh thường, đau buốt; d&ugrave;ng cải thảo v&agrave; rau cần nấu canh hoặc nấu ch&iacute;n lấy nước uống liền v&agrave;i ng&agrave;y.</li>\r\n</ul>\r\n\r\n<div id=\"gtx-trans\" style=\"left:0px; position:absolute; top:-4.8px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 0, '500g', '2020-12-09 19:39:31', '2020-12-30 07:10:46');
INSERT INTO `products` VALUES (68, 'Cần Tây', 'can-tay', 'upload/product/1607543055_rau-can-tay.jpg', 700, 46200, 45000, 4, 1, 1, 64, 59, NULL, 30, 22, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Cần t&acirc;y</strong> l&agrave; loại c&acirc;y thảo sống 1-2 năm c&oacute; th&acirc;n mọc đứng, cao khoảng 1m, c&oacute; r&atilde;nh dọc. Hoa m&agrave;u trắng hay xanh lục, xếp th&agrave;nh t&aacute;n.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>T&iacute;nh the m&aacute;t, vị ngọt đắng.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Cần t&acirc;y</strong> c&oacute; t&iacute;nh chất lọc m&aacute;u, điều h&ograve;a huyết, l&agrave;m giảm b&eacute;o, khai vị, bổ thần kinh, cung cấp chất kho&aacute;ng, chống hoại huyết, lợi ti&ecirc;u ho&aacute;, k&iacute;ch th&iacute;ch tuyến thượng thận, giải nhiệt, chống lỵ, lợi tiểu, điều kh&iacute; v&agrave; dẫn mật, chống thấp khớp v&agrave; kh&aacute;ng khuẩn, l&agrave;m liền sẹo...</li>\r\n	<li><strong>Cần t&acirc;y</strong> c&oacute; c&ocirc;ng dụng chữa mỡ trong m&aacute;u cao, chữa mụn nhọt, vi&ecirc;m nhiễm, chữa cảm c&uacute;m, vi&ecirc;m miệng, họng, vi&ecirc;m gan mạn, chữa c&aacute;c bệnh về đường h&ocirc; hấp, gi&uacute;p ngăn ngừa sỏi thận, gi&uacute;p xương khỏe mạnh, lợi tiểu, trị t&aacute;o b&oacute;n, chống ung thư...</li>\r\n	<li><strong>Cần t&acirc;y</strong> chứa chất chống oxy h&oacute;a, c&oacute; t&aacute;c dụng chăm s&oacute;c da, trị mụn đầu đen, trị n&aacute;m da.</li>\r\n</ul>', 0, '800g', '2020-12-09 19:44:15', '2020-12-30 07:15:11');
INSERT INTO `products` VALUES (69, 'Rau khoai lang', 'rau-khoai-lang', 'upload/product/1607543196_Rau-khoai-.png', 700, 22000, 20000, 5, 1, 1, 64, 59, NULL, 26, 21, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Rau lang</strong> l&agrave; rau từ đọt d&acirc;y khoai lang, th&acirc;n thảo, dạng d&acirc;y leo, mọc b&ograve; tr&ecirc;n mặt đất, l&aacute; mọc so le h&igrave;nh tim hoặc ch&acirc;n vịt, m&agrave;u xanh.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong> Ngọt</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Rau lang</strong> t&iacute;nh m&aacute;t c&oacute; c&ocirc;ng dụng thanh nhiệt giải độc, ngừa mụn trị mụn, trị t&aacute;o b&oacute;n, lợi cho hệ ti&ecirc;u h&oacute;a, nhiều chất xơ n&ecirc;n gi&uacute;p nhuận tr&agrave;ng rất tốt.</li>\r\n	<li><strong>Rau lang</strong> c&oacute; h&agrave;m lượng xơ, c&aacute;c cacbohydrat phức, protein, c&aacute;c vitamin A v&agrave; C, sắt, canxi c&oacute; t&aacute;c dụng bổi bổ sức khỏe cơ thể, thanh can, lợi mật, gi&uacute;p tăng cường thị lực, chữa bệnh v&agrave;ng da, phụ nữ kinh nguyện kh&ocirc;ng đều, nam giới di tinh.</li>\r\n	<li><strong>Rau lang</strong> tốt cho người tiểu đường, trong ngọn rau lang c&oacute; chất gần giống insulin, t&aacute;c dụng l&agrave;m giảm đường huyết.</li>\r\n	<li><strong>Rau lang</strong> c&oacute; t&aacute;c dụng lợi sữa, những b&agrave; mẹ đang cho con b&uacute; m&agrave; &iacute;t sữa, muốn c&oacute; nhiều sữa hơn, n&ecirc;n d&ugrave;ng rau lang x&agrave;o với thịt heo ăn sẽ c&oacute; t&aacute;c dụng rất hiệu quả. Rau lang gi&uacute;p b&agrave; bầu bớt ngh&eacute;n, giảm buồn n&ocirc;n v&igrave; c&oacute; vitamin B6.</li>\r\n</ul>', 0, '800g', '2020-12-09 19:46:36', '2020-12-30 07:15:18');
INSERT INTO `products` VALUES (70, 'Cải xanh', 'cai-xanh', 'upload/product/1607543319_cai xanh.jpg', 700, 37000, 35000, 6, 1, 0, 64, 59, NULL, 30, 26, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Cải xanh</strong>, hay c&ograve;n gọi l&agrave; cải bẹ xanh, cải cay, cải dưa, thuộc họ cải, l&agrave; loại cải chủ yếu được d&ugrave;ng để muối dưa. Cải bẹ dưa c&oacute; th&acirc;n to, l&aacute; c&oacute; m&agrave;u xanh đậm hoặc xanh n&otilde;n l&aacute; chuối.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Cay, đăng đắng.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Th&agrave;nh phần dinh dưỡng </strong>trong cải xanh gồm c&oacute;: vitamin A, B, C, K, axit nicotic, catoten, abumin..., n&ecirc;n được c&aacute;c chuy&ecirc;n gia dinh dưỡng khuy&ecirc;n d&ugrave;ng v&igrave; c&oacute; nhiều lợi &iacute;ch đối với sức khỏe cũng như c&oacute; t&aacute;c dụng ph&ograve;ng chống bệnh tật.</li>\r\n</ul>', 0, '500g', '2020-12-09 19:48:39', '2020-12-30 07:15:27');
INSERT INTO `products` VALUES (71, 'Bí đao', 'bi-dao', 'upload/product/1607543462_bi-dao.jpg', 700, 20000, 18500, 7, 1, 1, 65, 59, NULL, 28, 27, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; đao</strong> hay b&iacute; phấn hoặc b&iacute; trắng, l&agrave; lo&agrave;i thực vật thuộc họ Bầu B&iacute;, dạng d&acirc;y leo, tr&aacute;i ăn được. B&iacute; đao c&oacute; h&igrave;nh dạng tr&ograve;n, d&agrave;i, m&agrave;u xanh nhạt. B&iacute; đao l&agrave; một loại quả qu&yacute;, vừa c&oacute; thể d&ugrave;ng l&agrave;m thực phẩm lại c&oacute; thể l&agrave;m thuốc.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; đao</strong> chứa protit, đường, chất xơ, caroteen, vitamin PP, B1, B2, C, c&aacute;c chất canxi, sắt&hellip; B&iacute; đao c&oacute; vị ngọt, t&iacute;nh lạnh, kh&ocirc;ng độc, c&oacute; t&aacute;c dụng lợi tiểu, ti&ecirc;u ph&ugrave; thũng, thanh nhiệt, l&agrave;m m&aacute;t tim, ti&ecirc;u sưng mụn nhọt, l&agrave;m đẹp da v.v.</li>\r\n	<li><strong>B&iacute; đao</strong> kh&ocirc;ng chứa chất b&eacute;o, c&oacute; chứa hợp chất h&oacute;a học hyterin - caperic c&oacute; khả năng khống chế đường chuyển h&oacute;a th&agrave;nh mỡ n&ecirc;n ngăn chặn được sự t&iacute;ch lũy mỡ trong cơ thể, chống t&igrave;nh trạng b&eacute;o ph&igrave;.</li>\r\n</ul>', 0, '500g', '2020-12-09 19:51:02', '2020-12-30 07:15:34');
INSERT INTO `products` VALUES (72, 'Bí đỏ', 'bi-do', 'upload/product/1607543574_bi_do.jpg', 700, 28000, 26000, 8, 1, 1, 65, 59, NULL, 31, 26, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; đỏ Mỹ</strong> c&ograve;n gọi l&agrave; b&iacute; Hồ L&ocirc;, l&agrave; loại quả c&oacute; h&igrave;nh cầu hoặc h&igrave;nh trụ, khi ch&iacute;n tr&aacute;i b&iacute; c&oacute; m&agrave;u v&agrave;ng cam, b&ecirc;n ngo&agrave;i chia th&agrave;nh từng m&uacute;i, b&ecirc;n trong c&oacute; nhiều hạt, hạt dẹp, b&iacute; h&igrave;nh bầu dục c&oacute; chứa nhiều dầu.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, b&ugrave;i, thơm</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; đỏ</strong> c&oacute; rất nhiều dinh dưỡng: vitamin A gi&uacute;p cải thiện thị gi&aacute;c; ruột v&agrave; hạt b&iacute; đỏ c&oacute; chứa nhiều vitamin E gi&uacute;p củng cố hệ miễm nhiễm, ngăn ngừa sự xuất hiện những nếp nhăn sớm, bảo vệ da khỏi l&atilde;o h&oacute;a v&agrave; cải thiện chức năng hệ tim mạch. Vitamin K cần thiết để tổng hợp protit của m&aacute;u v&agrave; m&ocirc; xương. Vitamin T gi&uacute;p l&agrave;m đ&ocirc;ng m&aacute;u v&agrave; tạo c&aacute;c tế b&agrave;o m&aacute;u, ngăn ngừa một số dạng thiếu m&aacute;u.</li>\r\n	<li><strong>B&iacute; đỏ</strong> tốt cho xương v&agrave; mắt, gi&uacute;p giảm c&acirc;n, c&oacute; lợi cho tim mạch v&agrave; sự ph&aacute;t triển của n&atilde;o bộ, tăng cường hệ thống miễn dịch cho cơ thể, ph&ograve;ng ngừa tiểu đường, tẩy giun v&agrave; ngăn ngừa ung thư.</li>\r\n</ul>', 0, '500g', '2020-12-09 19:52:54', '2020-12-30 07:29:32');
INSERT INTO `products` VALUES (73, 'Bí ngòi xanh', 'bi-ngoi-xanh', 'upload/product/1607543686_bi-ngoi-huu-co-xanh.jpg', 700, 30000, 28000, 9, 1, 1, 65, 59, NULL, 26, 21, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; ng&ograve;i xanh</strong> c&oacute; h&igrave;nh dạng thu&ocirc;n, d&agrave;i. Da m&agrave;u xanh đậm với đốm trắng. B&iacute; ng&ograve;i gi&uacute;p chữa c&aacute;c bệnh về h&ocirc; hấp như hen suyễn, gi&uacute;p tr&aacute;nh nhồi m&aacute;u cơ tim v&agrave; đột quỵ, ngăn ngừa c&aacute;c bệnh về hoại huyết, th&acirc;m t&iacute;m bị g&acirc;y ra do thiếu hụt vitamin C. Chống l&atilde;o h&oacute;a, giảm c&acirc;n.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, gi&ograve;n.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; ng&ograve;i</strong> được cho l&agrave; loại thực phẩm l&yacute; tưởng nhất để ngăn ngừa chứng đa xơ cứng, ung thư ruột gi&agrave;, xơ vữa mạch, l&agrave;m hạ huyết &aacute;p.</li>\r\n	<li><strong>B&iacute; ng&ograve;i</strong> l&agrave; loại thực phẩm chống l&atilde;o h&oacute;a thượng thừa, những chất chống l&atilde;o h&oacute;a c&oacute; trong b&iacute; ngồi c&oacute; t&aacute;c dụng tăng cường tr&iacute; nhớ v&agrave; l&agrave;m thuy&ecirc;n giảm những chứng bệnh li&ecirc;n quan đến l&atilde;o h&oacute;a.</li>\r\n	<li><strong>B&iacute; ng&ograve;i</strong> chứa tr&ecirc;n 95% nước, rất l&yacute; tưởng cho những người đang thực hiện chế độ ăn ki&ecirc;ng, c&oacute; t&aacute;c dụng giảm c&acirc;n v&igrave; c&aacute;c chất dinh dưỡng trong b&iacute; ng&ograve;i cũng c&oacute; t&aacute;c dụng l&agrave;m tăng chuyển h&oacute;a.</li>\r\n</ul>', 0, '500g', '2020-12-09 19:54:46', '2020-12-30 07:29:48');
INSERT INTO `products` VALUES (74, 'Bí ngòi vàng', 'bi-ngoi-vang', 'upload/product/1607543915_bi_ngoi_vang_huu_co.jpg', 700, 48000, 45000, 10, 1, 1, 65, 59, NULL, 31, 26, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; ng&ograve;i v&agrave;ng</strong>&nbsp;c&oacute; h&igrave;nh dạng thu&ocirc;n, d&agrave;i. Da m&agrave;u v&agrave;ng tươi, b&oacute;ng. B&iacute; ng&ograve;i gi&uacute;p chữa c&aacute;c bệnh về h&ocirc; hấp như hen suyễn, gi&uacute;p tr&aacute;nh nhồi m&aacute;u cơ tim v&agrave; đột quỵ, ngăn ngừa c&aacute;c bệnh về hoại huyết, th&acirc;m t&iacute;m bị g&acirc;y ra do thiếu hụt vitamin C. Chống l&atilde;o h&oacute;a, giảm c&acirc;n.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, gi&ograve;n.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; ng&ograve;i </strong>được cho l&agrave; loại thực phẩm l&yacute; tưởng nhất để ngăn ngừa chứng đa xơ cứng, ung thư ruột gi&agrave;, xơ vữa mạch, l&agrave;m hạ huyết &aacute;p.</li>\r\n	<li><strong>B&iacute; ng&ograve;i </strong>l&agrave; loại thực phẩm chống l&atilde;o h&oacute;a thượng thừa, những chất chống l&atilde;o h&oacute;a c&oacute; trong b&iacute; ngồi c&oacute; t&aacute;c dụng tăng cường tr&iacute; nhớ v&agrave; l&agrave;m thuy&ecirc;n giảm những chứng bệnh li&ecirc;n quan đến l&atilde;o h&oacute;a.</li>\r\n	<li><strong>B&iacute; ng&ograve;i</strong> chứa tr&ecirc;n 95% nước, rất l&yacute; tưởng cho những người đang thực hiện chế độ ăn ki&ecirc;ng, c&oacute; t&aacute;c dụng giảm c&acirc;n v&igrave; c&aacute;c chất dinh dưỡng trong b&iacute; ng&ograve;i cũng c&oacute; t&aacute;c dụng l&agrave;m tăng chuyển h&oacute;a</li>\r\n</ul>', 0, '500g', '2020-12-09 19:58:35', '2020-12-30 07:29:59');
INSERT INTO `products` VALUES (75, 'Bí đỏ non', 'bi-do-non', 'upload/product/1607589100_Bi-do-non.jpg', 700, 40000, 36000, 11, 1, 0, 65, 59, NULL, 26, 21, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; đỏ non</strong> l&agrave; b&iacute; đỏ h&aacute;i khi c&ograve;n non. B&iacute; đỏ non l&agrave; loại thực phẩm đứng đầu về h&agrave;m lượng sắt, gi&agrave;u vitamin, muối kho&aacute;ng cũng như c&aacute;c ax&iacute;t hữu cơ.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, gi&ograve;n.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&iacute; đỏ non</strong> c&oacute; c&ocirc;ng dụng tr&aacute;nh cảm, vitamin nh&oacute;m B gi&uacute;p đấu tranh với mệt mỏi, c&aacute;u giận v&agrave; mất ngủ, củng cố t&oacute;c v&agrave; m&oacute;ng ch&acirc;n, m&oacute;ng tay.</li>\r\n	<li><strong>Vitamin A trong b&iacute; đỏ non </strong>gi&uacute;p cải thiện thị gi&aacute;c. Ruột v&agrave; hạt b&iacute; đỏ c&oacute; chứa nhiều vitamin E &ndash; một loại antioxidant tự nhi&ecirc;n gi&uacute;p củng cố hệ miễm nhiễm, ngăn ngừa sự xuất hiện những nếp nhăn sớm, bảo vệ da khỏi l&atilde;o h&oacute;a gi&uacute;p nhanh liền sẹo, v&agrave; cải thiện chức năng hệ tim mạch v&agrave; một số chứ năng kh&aacute;c.</li>\r\n	<li><strong>B&iacute; đỏ</strong> l&agrave; một loại thực phẩm v&agrave;ng, rất gi&agrave;u dinh dưỡng. Trong b&iacute; đỏ c&oacute; chứa tinh bột, protein, carotene, vitamin B, vitamin C, vitamin K, vitamin T, canxi, photpho v&agrave; nhiều chất dinh dưỡng kh&aacute;c tốt cho cơ thể. Ngo&agrave;i ra, b&iacute; đỏ c&ograve;n c&oacute; nhiều t&aacute;c dụng tốt cho sức khỏe v&agrave; sắc đẹp như: Bảo vệ ni&ecirc;m mạc dạ d&agrave;y, hạn chế v&agrave; kiểm so&aacute;t bệnh tiểu đường, gi&uacute;p nhuận tr&agrave;ng, tốt cho xương v&agrave; mắt, tốt cho n&atilde;o v&agrave; phụ nữ mang thai. Nếu muốn giảm c&acirc;n nhanh ch&oacute;ng m&agrave; kh&ocirc;ng cần lo lắng c&aacute;c vấn đề về sức khỏe kh&aacute;c đi k&egrave;m th&igrave; b&iacute; đỏ l&agrave; sự lựa chọn h&agrave;ng đầu.</li>\r\n</ul>', 0, '800g', '2020-12-10 08:31:40', '2020-12-30 07:30:09');
INSERT INTO `products` VALUES (76, 'Bông cải xanh', 'bong-cai-xanh', 'upload/product/1607589241_bong cai xanh.jpg', 700, 64000, 58000, 12, 1, 0, 65, 59, NULL, 28, 27, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&ocirc;ng cải xanh</strong>, c&ograve;n gọi l&agrave; s&uacute;p lơ xanh, l&agrave; một loại c&acirc;y thuộc họ cải, c&oacute; hoa lớn ở đầu, thường được d&ugrave;ng như rau.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, gi&ograve;n.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>B&ocirc;ng cải xanh</strong> rất gi&agrave;u vitamin A, C, tốt cho mắt, c&oacute; t&aacute;c dụng duy tr&igrave; vẻ đẹp của l&agrave;n da, tăng cường sức khỏe hệ miễn dịch, gi&uacute;p bạn tr&aacute;nh xa bệnh tật, gi&uacute;p giữ ẩm v&agrave; duy tr&igrave; độ đ&agrave;n hồi của l&agrave;n da, kiểm so&aacute;t nồng độ cholesterol trong m&aacute;u.</li>\r\n	<li><strong>Vitamin K</strong> trong b&ocirc;ng cải xanh gi&uacute;p qu&aacute; tr&igrave;nh đ&ocirc;ng m&aacute;u trong cơ thể diễn ra b&igrave;nh thường, bảo vệ gan. B&ocirc;ng cải xanh chứa nhiều chất xơ, hỗ trợ ti&ecirc;u h&oacute;a, giảm nguy cơ bệnh tim v&agrave; tiểu đường.</li>\r\n	<li><strong>B&ocirc;ng cải xanh</strong> c&oacute; chứa canxi, kali, gi&uacute;p tăng cường sức khỏe của xương, duy tr&igrave; t&igrave;nh trạng khỏe mạnh của c&aacute;c d&acirc;y thần kinh, gi&uacute;p co thắt cơ bắp v&agrave; ổn định lượng chất lỏng trong c&aacute;c tế b&agrave;o. Magie l&agrave; kho&aacute;ng chất giữ nhiều vai tr&ograve; quan trọng trong cơ thể, như điều chỉnh nhiệt độ cơ thể, l&agrave;m sạch độc tố, cung cấp năng lượng v&agrave; tăng cường sức khỏe của xương v&agrave; răng.</li>\r\n</ul>', 0, '800g', '2020-12-10 08:34:01', '2020-12-30 07:32:47');
INSERT INTO `products` VALUES (77, 'Bông cải trắng', 'bong-cai-trang', 'upload/product/1607589447_bong-cai-tra.jpg', 700, 80000, 70000, 13, 1, 1, 66, 59, NULL, 28, 27, '<p>GIỚI THIỆU SẢN PHẨM</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; B&ocirc;ng cải trắng hay c&ograve;n gọi l&agrave; s&uacute;p lơ trắng l&agrave; loại c&acirc;y c&oacute; phần b&ocirc;ng trắng kh&iacute;t, xốp v&agrave; mềm, b&ecirc;n ngo&agrave;i c&oacute; l&aacute; bao bọc xung quanh, phiến l&aacute; d&agrave;y, cứng, phần b&ocirc;ng được d&ugrave;ng để ăn c&ograve;n phần l&aacute; d&agrave;nh cho gia s&uacute;c.<br />\r\n<br />\r\n&nbsp;</p>', '<p>C&Ocirc;NG DỤNG</p>\r\n\r\n<p style=\"margin-left:40px\">&bull; B&ocirc;ng cải trắng đ&atilde; được biết đến từ l&acirc;u l&agrave; mang lại gi&aacute; trị dinh dưỡng v&agrave; c&oacute; lợi cho sức khỏe. Với h&agrave;m lượng calo v&agrave; chất xơ &iacute;t (khoảng 25 calo v&agrave; 2g chất xơ), đ&acirc;y l&agrave; m&oacute;n ăn tuyệt vời cho người muốn giảm c&acirc;n. Một số hoạt chất trong cải đ&atilde; được chứng minh hoạt động rất hiệu quả tr&ecirc;n hệ miễn dịch, tăng cường kh&aacute;ng vi&ecirc;m, chống vi tr&ugrave;ng v&agrave; vi khuẩn v&agrave; điều trị chứng loạn sản cổ tử cung.</p>\r\n\r\n<p style=\"margin-left:40px\">&bull; B&ocirc;ng cải trắng cung cấp một lượng lớn vitamin C gi&uacute;p chống oxy h&oacute;a đ&atilde; được chứng minh gi&uacute;p chống lại c&aacute;c gốc tự do c&oacute; hại, tăng cường khả năng miễn dịch, ngăn ngừa c&aacute;c bệnh nhiễm tr&ugrave;ng v&agrave; ngừa ung thư. B&ocirc;ng cải trắng chứa nhiều kho&aacute;ng chất như mangan, đồng, sắt v&agrave; kali, c&aacute;c nguy&ecirc;n tố vi lượng n&agrave;y cũng l&agrave; th&agrave;nh phần kh&ocirc;ng thể thiếu cho hoạt động của c&aacute;c enzym trong cơ thể, g&oacute;p phần bảo vệ khung xương, hồng cầu v&agrave; điều h&ograve;a huyết &aacute;p. B&ocirc;ng cải trắng gi&uacute;p l&agrave;m bền c&aacute;c th&agrave;nh mạch m&aacute;u. Một số vitamin kh&aacute;c gi&uacute;p k&iacute;ch hoạt hoạt động chống vi&ecirc;m trong mao mạch v&agrave; ngăn ngừa tổn thương mạch m&aacute;u ...<br />\r\n<br />\r\nLink nguồn :&nbsp;<a href=\"https://organicfood.vn/bong-cai-trang-huu-co.html\">https://organicfood.vn/bong-cai-trang-huu-co.html</a></p>', 0, '800g', '2020-12-10 08:37:27', '2020-12-30 07:32:37');
INSERT INTO `products` VALUES (78, 'Cà chua', 'ca-chua', 'upload/product/1607589606_ca_chua.png', 700, 43200, 40000, 14, 1, 1, 65, 59, NULL, 31, 26, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>C&agrave; chua H&agrave; Lan&nbsp;</strong>l&agrave; loại c&agrave; chua c&oacute; k&iacute;ch thước lớn, tr&aacute;i tr&ograve;n, dẹt, m&agrave;u đỏ tươi. Trọng lượng trung b&igrave;nh ~130gr/tr&aacute;i.&nbsp;C&agrave; chua H&agrave; Lan&nbsp;c&oacute; lượng dinh dưỡng cao; do đ&oacute;, c&ocirc;ng dụng rất phong ph&uacute;..</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt dịu, xen lẫn chua chua, bột.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>C&ocirc;ng dụng l&agrave;m đẹp như:</strong> chống l&atilde;o h&oacute;a, l&agrave;m da mịn m&agrave;ng tươi s&aacute;ng, bảo vệ bề mặt da.</li>\r\n	<li><strong>C&ocirc;ng dụng chữa bệnh như:</strong> chữa vi&ecirc;m gan m&atilde;n t&iacute;nh, hỗ trợ cho người bị vi&ecirc;m thận, chữa bệnh tim mạch, chữa b&iacute; đại tiện, thiếu m&aacute;u, chữa mụn nhọt, bỏng lửa, chữa sốt cao k&egrave;m theo kh&aacute;t nước hay chữa tăng huyết &aacute;p, chảy m&aacute;u ch&acirc;n răng.</li>\r\n</ul>', 0, '800g', '2020-12-10 08:40:06', '2020-12-30 07:32:23');
INSERT INTO `products` VALUES (79, 'Cà chua bi', 'ca-chua-bi', 'upload/product/1607589748_ca chua bi.jpg', 700, 65000, 60000, 15, 1, 0, 65, 59, NULL, 28, 27, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>C&agrave; chua bi cocktail</strong> l&agrave; loại c&agrave; chua c&oacute; k&iacute;ch thước nhỏ, tr&aacute;i tr&ograve;n, m&agrave;u đỏ tươi. C&agrave; chua bi c&oacute; lượng dinh dưỡng cao; do đ&oacute;, c&ocirc;ng dụng rất phong ph&uacute;..</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt dịu, xen lẫn chua chua, gi&ograve;n.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>C&ocirc;ng dụng l&agrave;m đẹp như:</strong> chống l&atilde;o h&oacute;a, l&agrave;m da mịn m&agrave;ng tươi s&aacute;ng, bảo vệ bề mặt da.</li>\r\n	<li><strong>C&ocirc;ng dụng chữa bệnh như:</strong> chữa vi&ecirc;m gan m&atilde;n t&iacute;nh, hỗ trợ cho người bị vi&ecirc;m thận, chữa bệnh tim mạch, chữa b&iacute; đại tiện, thiếu m&aacute;u, chữa mụn nhọt, bỏng lửa, chữa sốt cao k&egrave;m theo kh&aacute;t nước hay chữa tăng huyết &aacute;p, chảy m&aacute;u ch&acirc;n răng.</li>\r\n</ul>', 0, '800g', '2020-12-10 08:42:28', '2020-12-30 07:31:41');
INSERT INTO `products` VALUES (80, 'Cà rốt Nhật', 'ca-rot-nhat', 'upload/product/1607590012_ca rot.png', 700, 40000, 36000, 16, 1, 0, 65, 59, NULL, 31, 26, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>C&agrave; rốt</strong> l&agrave; loại c&acirc;y c&oacute; củ, củ to ở phần đầu v&agrave; nhọn ở phần đu&ocirc;i, củ c&agrave; rốt thường c&oacute; m&agrave;u cam hoặc đỏ, phần ăn được thường gọi l&agrave; củ nhưng thực chất đ&oacute; l&agrave; phần rễ của c&agrave; rốt.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Ngọt, thơm, gi&ograve;n</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>C&agrave; rốt </strong>chứa rất nhiều vitamin A, B, C rất tốt cho mắt, gi&uacute;p tăng cường v&agrave; bồi bổ thị lực, gi&uacute;p chuyển h&oacute;a v&agrave; t&aacute;i tạo da, tăng sức đề kh&aacute;ng, ph&ograve;ng v&agrave; trị c&aacute;c bệnh, gi&uacute;p bổ tỳ ti&ecirc;u thực, nhuận tr&agrave;ng, bổ can thận, minh mục, thanh nhiệt giải độc.</li>\r\n	<li><strong>C&agrave; rốt</strong> c&oacute; thể chữa được c&aacute;c bệnh như: cảm mạo, bệnh sởi, thủy đậu, ti&ecirc;u chảy. Trong c&agrave; rốt c&oacute; chứa insulin c&oacute; t&aacute;c dụng l&agrave;m giảm 1/3 lượng đường trong m&aacute;u, gi&uacute;p hạ huyết &aacute;p, l&agrave; thực phẩm l&yacute; tưởng cho người bị tiểu đường v&agrave; người bị cao huyết &aacute;p.</li>\r\n	<li>Nếu muốn c&oacute; một l&agrave;n da mịn m&agrave;ng, tươi s&aacute;ng hay một đ&ocirc;i mắt long lanh khỏe mạnh ta chỉ cần &eacute;p c&agrave; rốt lấy nước v&agrave; d&ugrave;ng hằng ng&agrave;y</li>\r\n</ul>', 0, '500g', '2020-12-10 08:46:52', '2020-12-30 07:31:06');
INSERT INTO `products` VALUES (81, 'Chanh không hạt', 'chanh-khong-hat', 'uploads/product/1607958105_canh không hạt.png', 700, 50000, 48000, 17, 1, 0, 65, 59, NULL, 28, 27, NULL, NULL, 0, '500g', '2020-12-10 09:03:56', '2020-12-30 07:30:45');
INSERT INTO `products` VALUES (82, 'Ớt chuông đỏ', 'ot-chuong-do', 'upload/product/1607591343_ot_chuong_do.jpg', 700, 70000, 65000, 18, 1, 0, 65, 59, NULL, 26, 27, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Ớt chu&ocirc;ng</strong> về mặt thực vật học l&agrave; tr&aacute;i c&acirc;y, nhưng lại thường được xem l&agrave; rau quả trong lĩnh vực nấu nướng. Đ&acirc;y l&agrave; loại quả c&oacute; lượng vitamin C kỷ lục. Ớt chu&ocirc;ng c&oacute; m&agrave;u sắc đa dạng: xanh, đỏ, v&agrave;ng, cam, hiếm hơn th&igrave; c&oacute; thể l&agrave; m&agrave;u n&acirc;u, trắng, cầu vồng, m&agrave;u hoa Oải hương, v&agrave; t&iacute;m sẫm.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>Thơm, gi&ograve;n, ngọt, &iacute;t hăng, kh&ocirc;ng cay.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Ớt chu&ocirc;ng</strong> cũng rất gi&agrave;u vitamin A, protid, đường, canxi, photpho, sắt, beta-caroten, vitamin B1, B2... Một quả ớt chu&ocirc;ng v&agrave;ng&nbsp;c&oacute; chứa nhiều vitamin C, chất xơ v&agrave; vitamin B6 v&agrave; c&aacute;c dưỡng chất kh&aacute;c hơn ớt chu&ocirc;ng xanh.</li>\r\n	<li><strong>C&ocirc;ng dụng của Ớt chu&ocirc;ng:</strong> tăng cường trao đổi chất, chất chống oxy h&oacute;a, ngừa ung thư, giảm đau, tăng cường miễn dịch. Ớt chu&ocirc;ng v&agrave;ng&nbsp;rất gi&agrave;u carotenoid c&oacute; thể gi&uacute;p ngăn ngừa nếp nhăn v&agrave; tăng cường lưu th&ocirc;ng m&aacute;u đến da của bạn, bảo vệ m&agrave;ng collagen v&agrave; nu&ocirc;i dưỡng da hiệu quả gi&uacute;p bạn tr&ocirc;ng trẻ trung hơn</li>\r\n	<li><strong>Ớt chu&ocirc;ng</strong> gi&uacute;p kiểm so&aacute;t tốt bệnh tiểu đường v&agrave; lượng cholesterol, gi&uacute;p chống lại c&aacute;c gốc tự do trong cơ thể v&agrave; điều trị c&aacute;c bệnh tim mạch, vi&ecirc;m xương khớp... v&agrave; cũng hỗ trợ hấp thu chất sắt.</li>\r\n</ul>', 0, '800g', '2020-12-10 09:09:03', '2020-12-30 07:30:57');
INSERT INTO `products` VALUES (83, 'Xà lách Roman', 'xa-lach-roman', 'upload/product/1607591485_xa_lach_roman.jpg', 700, 82000, 75000, 19, 1, 0, 67, 59, NULL, 28, 27, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>X&agrave; l&aacute;ch Romaine</strong> c&oacute; m&agrave;u xanh tươi đậm, l&aacute; tr&ograve;n, d&agrave;i v&agrave; cuốn b&uacute;p. Vị gi&ograve;n, ngọt v&agrave; thơm. Đ&acirc;y l&agrave; loại x&agrave; l&aacute;ch ch&iacute;nh trong m&oacute;n Caesar salad. X&aacute; l&aacute;ch c&oacute; nhiều vitamins v&agrave; kho&aacute;ng chất n&ecirc;n bạn c&oacute; thể d&ugrave;ng tuỳ th&iacute;ch..</li>\r\n	<li><strong>Hương vị:&nbsp;</strong> Gi&ograve;n, ngọt.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>X&agrave; l&aacute;ch Romaine</strong> gi&agrave;u Vitamin A, K, C, B1, B2, axit folic, Magi&ecirc;, Cr&ocirc;m, chất kho&aacute;ng v&agrave; chất xơ. Hổ trợ ti&ecirc;u h&oacute;a, tốt cho gan, giảm triệu chứng mất ngủ, cholesterol cao. Tốt cho tim mạch.</li>\r\n	<li><strong>X&agrave; l&aacute;ch Romaine</strong> với l&aacute; xanh đậm c&oacute; nhiều chất chống oxy ho&aacute; hổ trợ ph&ograve;ng ngừa ung thư, đặc biệt l&agrave; ung thư gan v&agrave; ruột gi&agrave;.</li>\r\n</ul>', 0, '800g', '2020-12-10 09:11:25', '2020-12-30 07:30:37');
INSERT INTO `products` VALUES (84, 'Hành lá', 'hanh-la', 'uploads/product/1607958180_hanh_la.jpg', 700, 20000, 18000, 20, 1, 0, 68, 59, NULL, 28, 27, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>H&agrave;nh l&aacute;</strong> l&agrave; một loại gia vị quen thuộc trong bữa ăn người Việt.</li>\r\n	<li><strong>H&agrave;nh l&aacute;</strong> th&acirc;n thẳng, l&aacute; d&agrave;i khoảng 30-50 cm. Cuống h&agrave;nh l&aacute; m&agrave;u trắng, l&aacute; m&agrave;u xanh, h&igrave;nh trụ rỗng, ph&iacute;a gốc l&aacute; ph&igrave;nh to, tr&ecirc;n đầu thu&ocirc;n nhọn, mỗi c&acirc;y c&oacute; khoảng 5-6 l&aacute;.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong> Cay, thơm nồng, hơi hăng.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>H&agrave;nh l&aacute;</strong> c&ograve;n được gọi l&agrave; h&agrave;nh xanh, h&agrave;nh ta. H&agrave;nh l&aacute; dễ trồng tại nh&agrave; để sử dụng cho bữa ăn h&agrave;ng ng&agrave;y.</li>\r\n	<li><strong>H&agrave;nh l&aacute;</strong> chứa nhiều vitamin A, B6, C, K, c&oacute; c&ocirc;ng dụng chữa bệnh như: gi&uacute;p xương chắc khỏe, bảo vệ tim, điều h&ograve;a lượng m&aacute;u, ngăn ngừa ung thư., chống vi&ecirc;m, tăng cường miễn dịch, tốt cho mắt, thải độc qua tuyến mồ h&ocirc;i, chống bệnh tiểu đường.</li>\r\n</ul>', 0, '200g', '2020-12-10 09:16:26', '2020-12-30 07:30:30');
INSERT INTO `products` VALUES (85, 'Sả', 'sa', 'uploads/product/1607958165_sa.png', 700, 10000, 9500, 21, 1, 0, 68, 59, NULL, 31, 26, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Sả</strong> cũng l&agrave; một loại c&acirc;y gia vị cho c&aacute;c m&oacute;n ăn. Sả mọc từng bụi lớn, l&aacute; h&igrave;nh m&aacute;c d&agrave;i, m&eacute;p l&aacute; sắc. Gốc sả l&agrave; những cuống l&aacute; cuộn chặt với nhau. Phần gốc sả sử dụng để l&agrave;m m&oacute;n ăn d&agrave;i khoảng 20-25 cm. C&oacute; 2 loại sả trắng v&agrave; sả t&iacute;m.</li>\r\n	<li><strong>Hương vị:&nbsp;</strong> Cay nồng, thơm.</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Sả</strong> c&oacute; c&ocirc;ng dụng l&agrave;m đẹp da, d&ugrave;ng để x&ocirc;ng hơi, trị mụn, giảm c&acirc;n.</li>\r\n	<li><strong>C&ocirc;ng dụng chữa bệnh như:</strong> ph&ograve;ng chống ung thư, ti&ecirc;u h&oacute;a tốt, chữa đau bụng, giải độc, c&oacute; lợi cho hệ thần kinh, giảm đau, hạ sốt, chống nấm chống khuẩn.</li>\r\n</ul>', 0, '200g', '2020-12-10 09:17:35', '2020-12-30 07:33:01');
INSERT INTO `products` VALUES (86, 'Nấm đùi gà', 'nam-dui-ga', 'upload/product/1607592439_nam_dui_ga.jpg', 700, 44000, 40000, 22, 1, 0, 69, 59, NULL, 31, 26, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Nấm Đ&ugrave;i g&agrave;</strong> c&ograve;n c&oacute; t&ecirc;n l&agrave; nấm S&ograve; vua, t&ecirc;n tiếng Anh l&agrave; King oyster mushroom, được mệnh danh l&agrave; nữ ho&agrave;ng của c&aacute;c loại nấm. Mũ nấm l&uacute;c c&ograve;n non c&oacute; m&agrave;u x&aacute;m đen v&agrave; chuyển dần sang x&aacute;m n&acirc;u khi trưởng th&agrave;nh. Th&acirc;n nấm d&agrave;i, m&agrave;u trắng, to giống như đ&ugrave;i g&agrave; n&ecirc;n c&ograve;n c&oacute; t&ecirc;n gọi l&agrave; nấm Đ&ugrave;i g&agrave;.</li>\r\n	<li><strong>Nấm</strong> c&oacute; gi&aacute; trị dinh dưỡng cao, h&agrave;m lượng protein gấp 4-6 lần loại rau th&ocirc;ng thường kh&aacute;c&hellip;</li>\r\n	<li><strong>Hương vị:&nbsp;</strong>ngọt, gi&ograve;n</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Nấm Đ&ugrave;i g&agrave;</strong> t&iacute;nh &ocirc;n, chủ trị chứng ti&ecirc;u kh&aacute;t (bệnh tiểu đường), nấm Đ&ugrave;i g&agrave; c&oacute; hiệu quả chống ung thư, điều tiết sức miễn dịch của cơ thể v&agrave; cải thiện tuần ho&agrave;n m&aacute;u, đường đa nấm Đ&ugrave;i g&agrave; l&agrave; th&agrave;nh phần c&ocirc;ng hiệu ch&iacute;nh trong đ&oacute;.</li>\r\n	<li><strong>Nấm Đ&ugrave;i g&agrave;</strong> gi&uacute;p tăng cường chức năng đường ruột, dạ d&agrave;y, hỗ trợ ti&ecirc;u ho&aacute;, k&iacute;ch th&iacute;ch nhu cầu ăn l&agrave;m cho ăn ngon miệng. Cải thiện tuần ho&agrave;n m&aacute;u, giảm mỡ trong m&aacute;u, điều chỉnh huyết &aacute;p, điều tiết miễn dịch.</li>\r\n	<li><strong>Nấm Đ&ugrave;i G&agrave;</strong> ngo&agrave;i việc l&agrave; một loại nấm ăn ngon,th&igrave; c&ograve;n c&oacute; gi&aacute; trị dinh dưỡng cao. H&agrave;m lượng protein trong nấm đ&ugrave;i g&agrave; gấp 4-6 lần loại rau th&ocirc;ng thường kh&aacute;c... &nbsp; Nấm Đ&ugrave;i G&agrave; c&oacute; chứa nhiều vitamin, amino acid thiết yếu cho cơ thể v&agrave; kho&aacute;ng chất gi&uacute;p tăng cường tr&iacute; lực, lưu th&ocirc;ng kh&iacute; huyết. C&aacute;c nh&agrave; khoa học cũng cho biết chiết xuất từ nấm Đ&ugrave;i g&agrave; c&oacute; t&aacute;c dụng ngăn ngừa ung thư rất tốt..</li>\r\n</ul>', 0, '800g', '2020-12-10 09:27:19', '2020-12-30 07:33:15');
INSERT INTO `products` VALUES (87, 'Nấm Kim châm', 'nam-kim-cham', 'upload/product/1607592591_nam-kim-cham-han-quoc.png', 700, 30000, 25000, 23, 1, 0, 69, 59, NULL, 28, 27, NULL, NULL, 0, '200g', '2020-12-10 09:29:51', '2020-12-30 07:33:28');
INSERT INTO `products` VALUES (88, 'Vú sữa', 'vu-sua', 'upload/product/1607592782_Vu sua.jpg', 700, 80000, 75000, 24, 1, 1, 71, 61, NULL, 28, 27, '<h2>C&ocirc;ng dụng quả v&uacute; sữa</h2>\r\n\r\n<h3>V&uacute; sữa chứa c&aacute;c dưỡng chất gi&uacute;p cơ thể khỏe mạnh</h3>\r\n\r\n<p>Ở phần thịt quả v&uacute; sữa c&oacute; c&aacute;c dưỡng chất như: sắt, photpho, calo, protein, magie,&hellip; Khi ăn v&uacute; sữa, c&aacute;c dưỡng chất n&agrave;y sẽ được ch&uacute;ng ta hấp thu gi&uacute;p cơ thể được khỏe mạnh, tăng sức đề kh&aacute;ng. Ngo&agrave;i ra, v&uacute; sữa c&ograve;n rất tốt cho trẻ em v&agrave; phụ nữ đang c&oacute; thai v&agrave; cho con b&uacute;.</p>\r\n\r\n<p>Loại&nbsp;<a href=\"https://gianongsan.org/trai-cay/\">tr&aacute;i c&acirc;y</a>&nbsp;n&agrave;y ngo&agrave;i c&aacute;c dưỡng chất tr&ecirc;n c&ograve;n c&oacute; vitamin C, vitamin A, vitamin nh&oacute;m B, gi&uacute;p cho cơ thể c&oacute; sức đề kh&aacute;ng tốt, s&aacute;ng da, tr&aacute;nh c&aacute;c bệnh thị lực. Chất chống oxy h&oacute;a c&oacute; trong v&uacute; sữa sẽ gi&uacute;p ngăn ngừa bệnh ung thư, gi&uacute;p da hồng h&agrave;o hơn, trị c&aacute;c bệnh nhiễm tr&ugrave;ng.</p>\r\n\r\n<h3>Gi&agrave;u chất xơ</h3>\r\n\r\n<p>Lượng chất xơ v&agrave; nước c&oacute; trong quả v&uacute; sữa cao, từ đ&oacute; quả v&uacute; sữa sẽ gi&uacute;p giảm được glucose trong m&aacute;u. Ngo&agrave;i ra, nhờ v&agrave;o lượng chất xơ n&agrave;y, bạn c&oacute; thể trị t&aacute;o b&oacute;n, tr&aacute;nh ăn kh&ocirc;ng ti&ecirc;u.</p>\r\n\r\n<p>Ngo&agrave;i ra, v&igrave; h&agrave;m lượng chất xơ c&oacute; trong v&uacute; sữa rất cao sẽ l&agrave;m dịu dạ d&agrave;y, gi&uacute;p ti&ecirc;u h&oacute;a chậm v&agrave; hấp thu dưỡng chất nhiều hơn, bạn sẽ kh&ocirc;ng c&oacute; cảm gi&aacute;c đ&oacute;i, gi&uacute;p giảm c&acirc;n hiệu quả</p>\r\n\r\n<h3>V&uacute; sữa gi&uacute;p xương chắc khỏe</h3>\r\n\r\n<p>Theo c&aacute;c nh&agrave; khoa học, lượng canxi trung b&igrave;nh c&oacute; trong 100g v&uacute; sữa l&agrave; 18mg canxi. V&igrave; thế, ăn quả v&uacute; sữa sẽ gi&uacute;p cho xương được chắc khỏe, đặc biệt l&agrave; trẻ em đang trong độ tuổi ph&aacute;t triển.</p>', NULL, 0, '800g', '2020-12-10 09:33:02', '2020-12-30 07:28:08');
INSERT INTO `products` VALUES (89, 'Xoài', 'xoai', 'upload/product/1607592985_qua_xoai.png', 700, 40000, 36000, 25, 1, 0, 71, 61, NULL, 28, 27, '<h2>Chống ung thư</h2>\r\n\r\n<p>Theo một nghi&ecirc;n cứu được tiến h&agrave;nh bởi Khoa Dinh dưỡng của Trường Y tế c&ocirc;ng cộng Harvard, một chế độ ăn gi&agrave;u beta-carotene (chất c&oacute; trong xo&agrave;i) cũng c&oacute; thể đ&oacute;ng vai tr&ograve; chống lại ung thư tuyến tiền liệt. Hơn nữa, trong một nghi&ecirc;n cứu được thực hiện bởi Texas AgriLife Research, c&aacute;c nh&agrave; khoa học thực phẩm đ&atilde; thử nghiệm chiết xuất polypheno từ xo&agrave;i tr&ecirc;n đại tr&agrave;ng, v&uacute;, phổi, bệnh bạch cầu v&agrave; m&ocirc; ung thư tuyến tiền liệt; xo&agrave;i đ&atilde; được chứng minh l&agrave; c&oacute; một số t&aacute;c động đối với tất cả c&aacute;c bệnh ung thư được thử nghiệm nhưng hiệu quả nhất với ung thư v&uacute; v&agrave; ung thư ruột kết.</p>\r\n\r\n<h2>Gi&uacute;p chắc xương</h2>\r\n\r\n<p>Một nghi&ecirc;n cứu đ&aacute;ng kinh ngạc từ Đại học bang Oklahoma đ&atilde; kh&aacute;m ph&aacute; vai tr&ograve; của chế độ ăn uống trong điều trị bệnh lo&atilde;ng xương, Trong khi cho chuột ăn 1 trong 6 chế độ ăn nhiều chất b&eacute;o, c&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; bao gồm xo&agrave;i sấy kh&ocirc; trong chế độ ăn của chuột. Những con chuột c&oacute; 1% xo&agrave;i trong chế độ ăn nhiều chất b&eacute;o của ch&uacute;ng cho thấy BMD [mật độ kho&aacute;ng xương] to&agrave;n bộ cơ thể, cột sống v&agrave; xương ch&agrave;y đều cao hơn.</p>\r\n\r\n<p>Điều th&uacute; vị ở đ&acirc;y l&agrave; những con chuột được cho ăn 1% xo&agrave;i c&oacute; kết quả củng cố xương tốt hơn so với những con nhận được 10% xo&agrave;i. Điều n&agrave;y c&oacute; nghĩa l&agrave; nếu ăn nhiều hơn kh&ocirc;ng nhất thiết l&agrave; tốt hơn bởi v&igrave; xo&agrave;i c&oacute; nhiều đường v&agrave; qu&aacute; nhiều đường g&acirc;y hại cho xương của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Tốt cho người bị tiểu đường</h2>\r\n\r\n<p>Xo&agrave;i gần đ&acirc;y được x&aacute;c định l&agrave; một loại thực phẩm đặc biệt hữu &iacute;ch trong cuộc chiến chống lại bệnh tiểu đường v&agrave; một nghi&ecirc;n cứu mới được thực hiện tại &Uacute;c đ&atilde; cụ thể h&oacute;a điều n&agrave;y. Ăn 1 quả xo&agrave;i mỗi ng&agrave;y c&oacute; thể gi&uacute;p giảm cholesterol cũng như chống lại bệnh tiểu đường. Nghi&ecirc;n cứu được thực hiện bởi TS. Ashley Wilkinson tại Đại học Queensland chỉ ra rằng, c&aacute;c th&agrave;nh phần trong xo&agrave;i hoạt động tương tự như một số loại thuốc trị tiểu đường v&agrave; cholesterol.</p>\r\n\r\n<h2>Gi&uacute;p cho hệ ti&ecirc;u h&oacute;a</h2>\r\n\r\n<p>T&aacute;c dụng của xo&agrave;i đối với hệ ti&ecirc;u h&oacute;a. Xo&agrave;i chứa một nh&oacute;m c&aacute;c enzyme ti&ecirc;u h&oacute;a được gọi l&agrave; amylase. Enzyme ti&ecirc;u h&oacute;a ph&aacute; vỡ c&aacute;c ph&acirc;n tử thực phẩm lớn để ch&uacute;ng c&oacute; thể dễ d&agrave;ng được hấp thụ. Amylase ph&acirc;n hủy carbs phức th&agrave;nh đường, chẳng hạn như glucose v&agrave; maltose. C&aacute;c enzyme n&agrave;y hoạt động mạnh hơn trong xo&agrave;i ch&iacute;n n&ecirc;n ch&uacute;ng ngọt hơn so với những quả chưa ch&iacute;n.</p>\r\n\r\n<p>Hơn nữa, v&igrave; xo&agrave;i chứa nhiều nước v&agrave; chất xơ, n&oacute; c&oacute; thể gi&uacute;p giải quyết c&aacute;c vấn đề ti&ecirc;u h&oacute;a như t&aacute;o b&oacute;n v&agrave; ti&ecirc;u chảy. Một nghi&ecirc;n cứu k&eacute;o d&agrave;i 4 tuần ở những người trưởng th&agrave;nh bị t&aacute;o b&oacute;n mạn t&iacute;nh cho thấy rằng ăn xo&agrave;i h&agrave;ng ng&agrave;y c&oacute; hiệu quả trong việc l&agrave;m giảm c&aacute;c triệu chứng của t&igrave;nh trạng n&agrave;y hơn l&agrave; một chất bổ sung c&oacute; chứa một lượng chất xơ h&ograve;a tan tương tự.</p>\r\n\r\n<h2>Giảm nguy cơ bị bệnh tim</h2>\r\n\r\n<p>Xo&agrave;i được coi l&agrave; tốt cho tim v&igrave; nồng độ polyphenol cao (chủ yếu l&agrave; mangiferin, quercetin, gallotannin v&agrave; axit gallic), c&aacute;c hợp chất hoạt t&iacute;nh sinh học l&agrave; chất chống oxy h&oacute;a, chống vi&ecirc;m v&agrave; chống nhiễm tr&ugrave;ng.</p>\r\n\r\n<p>Trong nghi&ecirc;n cứu mới của UC Davis, c&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; tuyển dụng 24 phụ nữ &nbsp;khỏe mạnh đ&atilde; m&atilde;n kinh. Họ ăn 2 cốc xo&agrave;i mỗi ng&agrave;y trong 2 tuần. Sau giai đoạn n&agrave;y, phụ nữ tiếp tục chế độ ăn b&igrave;nh thường nhưng hạn chế xo&agrave;i trong 13 ng&agrave;y.</p>\r\n\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; ghi lại nhịp tim v&agrave; huyết &aacute;p đồng thời họ đ&atilde; lấy mẫu m&aacute;u v&agrave; hơi thở.</p>\r\n\r\n<p>Trong thời gian ti&ecirc;u thụ xo&agrave;i 2 tuần, huyết &aacute;p trung b&igrave;nh của những người tham gia đ&atilde; giảm. Cụ thể, huyết &aacute;p t&acirc;m thu đ&atilde; giảm chỉ sau 2 giờ sau khi họ ăn xo&agrave;i mỗi ng&agrave;y. Ngo&agrave;i ra, 6 trong số 24 người tham gia thải ra m&ecirc;-tan cho thấy những thay đổi t&iacute;ch cực về sức khỏe đường ruột.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; nghi&ecirc;n cứu đầu ti&ecirc;n cho thấy t&aacute;c dụng của xo&agrave;i tốt cho tim mạch. Trong tương lai, c&aacute;c nh&agrave; nghi&ecirc;n cứu c&oacute; kế hoạch tiến h&agrave;nh nhiều nghi&ecirc;n cứu hơn với c&aacute;c nh&oacute;m d&acirc;n số kh&aacute;c.</p>\r\n\r\n<h2>Chống hen suyễn</h2>\r\n\r\n<p>Trong xo&agrave;i c&oacute; chứa chất beta-carotene giảm nguy cơ ph&aacute;t triển bệnh hen suyễn</p>\r\n\r\n<h2>T&oacute;c v&agrave; da khỏe đẹp</h2>\r\n\r\n<p>Xo&agrave;i cũng rất tốt cho t&oacute;c của bạn v&igrave; ch&uacute;ng c&oacute; chứa vitamin A - một chất dinh dưỡng cần thiết cho việc sản xuất b&atilde; nhờn gi&uacute;p giữ ẩm cho t&oacute;c. Vitamin A cũng cần thiết cho sự tăng trưởng của tất cả c&aacute;c m&ocirc; cơ thể, bao gồm cả da v&agrave; t&oacute;c.</p>\r\n\r\n<p>Hấp thụ đủ vitamin C c&oacute; trong 165g xo&agrave;i mỗi ng&agrave;y l&agrave; cần thiết cho việc tạo ra v&agrave; duy tr&igrave; collagen, cung cấp cấu tr&uacute;c cho da v&agrave; t&oacute;c.</p>', NULL, 0, '800g', '2020-12-10 09:36:25', '2020-12-30 07:28:37');
INSERT INTO `products` VALUES (90, 'Táo NewZeland', 'tao-newzeland', 'upload/product/1607593350_Tao_Newzealand.png', 700, 69000, 60000, 26, 1, 1, 72, 61, NULL, 28, 27, '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">Th&ocirc;ng Tin Sản Phẩm</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>Xuất xứ: </strong></li>\r\n	<li><strong>T&aacute;o NewZeland</strong> được mệnh danh l&agrave; loại t&aacute;o ngon độc nhất v&ocirc; nhị của thế giới lo&agrave;i t&aacute;o,đem đi biếu,đi tặng đều hay</li>\r\n	<li><strong>Đặc điểm:</strong><strong>&nbsp;</strong>T&aacute;o Envy quả tr&ograve;n, m&agrave;u đỏ, vỏ mỏng b&oacute;ng, chắc thịt, ăn gi&ograve;n, ngọt đậm, rất ph&ugrave; hợp với khẩu vị người Việt Nam.</li>\r\n	<li><strong>Hương Vị :</strong>&nbsp;gi&ograve;n, ngọ</li>\r\n</ul>', '<h1 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"color:#35a647\"><span style=\"background-color:#ffffff\">C&ocirc;ng Dụng</span></span></strong></span></span></h1>\r\n\r\n<ul>\r\n	<li><strong>T&aacute;o chứa nhiều chất chống oxy h&oacute;a</strong> ngăn chặn tế b&agrave;o v&agrave; tổn thương m&ocirc; khỏi c&aacute;c gốc tự do trong cơ thể bằng c&aacute;ch ngăn chặn hoặc l&agrave;m giảm qu&aacute; tr&igrave;nh oxy h&oacute;a.&nbsp;Quả t&aacute;o gi&uacute;p giảm nguy cơ bệnh tim, tiểu đường v&agrave; ung thư.</li>\r\n	<li><strong>Vitamin v&agrave; c&aacute;c kho&aacute;ng chất </strong>trong t&aacute;o cung cấp 2.000 calo mỗi ng&agrave;y, một quả t&aacute;o Envy, trung b&igrave;nh cung cấp &nbsp;cho 8% gi&aacute; trị vitamin C, 2% Fe v&agrave; Vitamin A, 5% kali.</li>\r\n	<li><strong>Carbohydrate v&agrave; chất xơ tự nhi&ecirc;n</strong> c&oacute; trong t&aacute;o Envy l&agrave; pectin.Pectin c&oacute; nhiều trong t&aacute;o gi&uacute;p l&agrave;m giảm huyết &aacute;p v&agrave; ngăn ngừa ung thư ruột kết.&nbsp;</li>\r\n	<li><strong>Ngo&agrave;i ra</strong> t&aacute;o cũng chứa Phytochemical v&agrave; Flavonoids c&oacute; chức năng chống dị ứng, chống vi&ecirc;m, chống khối u v&agrave; c&aacute;c hiệu ứng chất chống oxy h&oacute;a tr&ecirc;n cơ thể v&agrave; bảo vệ chống lại qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a.</li>\r\n</ul>', 0, 'kg', '2020-12-10 09:42:30', '2020-12-30 07:28:47');
INSERT INTO `products` VALUES (91, 'Cherry', 'cherry', 'upload/product/1607593471_cherry.jpg', 700, 500000, 480000, 27, 1, 1, 72, 61, NULL, 28, 27, '<p><strong>7&nbsp;<strong>t&aacute;c dụng</strong>&nbsp;của&nbsp;<strong>cherry</strong>&nbsp;khiến bạn muốn ăn thử ngay</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Quả cherry</strong>&nbsp;chứa c&aacute;c chất chống oxy h&oacute;a. ...</li>\r\n	<li><strong>T&aacute;c dụng</strong>&nbsp;của&nbsp;<strong>cherry</strong>&nbsp;gi&uacute;p trị tiểu đường. ...</li>\r\n	<li><strong>T&aacute;c dụng</strong>&nbsp;của&nbsp;<strong>cherry</strong>&nbsp;gi&uacute;p bạn ngủ ngon.</li>\r\n	<li><strong>T&aacute;c dụng</strong>&nbsp;của&nbsp;<strong>cherry</strong>&nbsp;gi&uacute;p giảm đau khớp. ...</li>\r\n	<li><strong>C&ocirc;ng dụng</strong>&nbsp;của&nbsp;<strong>cherry</strong>&nbsp;gi&uacute;p giảm nguy cơ bệnh g&uacute;t. ...</li>\r\n	<li><strong>Quả cherry</strong>&nbsp;gi&uacute;p giảm lượng cholesterol. ...</li>\r\n	<li><strong>T&aacute;c dụng</strong>&nbsp;của&nbsp;<strong>cherry</strong>&nbsp;gi&uacute;p giảm đau sau khi tập luyện.</li>\r\n</ul>', NULL, 0, 'kg', '2020-12-10 09:44:31', '2020-12-30 07:28:55');

